import React, { useState } from 'react';
import ReactDOM from 'react-dom';
import APICall from './util';
import BootstrapTable from 'react-bootstrap-table-next';
import paginationFactory from 'react-bootstrap-table2-paginator';
import ReactModal from 'react-modal';
import './index.css';
import { products, columns } from './data';

class CustomerData extends React.Component {
    render() {
        console.log("Props: ", this.props);
        return (
            <div className="customer-data">
                {this.props.data.city}
                <button onClick={this.props.onClick} >X</button>
            </div>
        );
    }
}

class Panel extends React.Component {


    constructor(props) {
        super(props);
        this.state = {
            customers: [],
            currentCustomerIndex: null,
            showModal: false,
        };
        this.handleOpenModal = this.handleOpenModal.bind(this);
        this.handleCloseModal = this.handleCloseModal.bind(this);
        const [isOpen, setIsOpen] = useState(false);
    }

    // isOpen = useState(false);
    // setIsOpen = useState(false);

    // const [isOpen, setIsOpen] = useState(false);
    toggleModal() {
        setIsOpen(!isOpen);
    }


    handleOpenModal(index) {
        this.setState({ showModal: true, currentCustomerIndex: index });
    }

    handleCloseModal() {
        this.setState({ showModal: false, currentCustomerIndex: null });
    }

    render() {

        let customers = products.slice();
        // this.setState({ customers: customers });
        this.state.customers = customers;

        APICall().then((data) => {
            this.setState({ customers: data });
        });

        for (let i = 0; i < customers.length; i++) {
            let currentProduct = products[i];
            currentProduct["view_button"] = <button onClick={() => this.handleOpenModal(i)}>View</button>
        }

        return (
            <Modal
                isOpen={isOpen}
                onRequestClose={toggleModal}
                contentLabel="My dialog">

            </Modal>
        )

        if (!this.state.showModal) {
            return (
                <div>
                    <BootstrapTable striped bordered hover
                        keyField="city"
                        data={customers}
                        columns={columns}
                    />
                </div>
            )
        } else {
            return (
                <ReactModal id="modal" isOpen={this.state.showModal} contentLabel="Minimal Modal Example">
                    <CustomerData data={this.state.customers[this.state.currentCustomerIndex]} onClick={this.handleCloseModal} />
                </ReactModal>
            )
        }

    }

}

ReactDOM.render(
    <Panel />,
    document.getElementById('root')
);
