import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';

class Customer extends React.Component {
    render() {
        return (
            <div className="customer">
                {this.props.value}
                <button onClick={this.props.onClick}>VIEW</button>
            </div>
        );
    }
}

class CustomerData extends React.Component {
    render() {
        return (
            <div className="customer-data">
                {this.props.customerData.city}
            </div>
        );
    }
}

class Panel extends React.Component {

    constructor(props) {
        super(props);
        this.state = {
            customers: [
                {
                    "province": "ON",
                    "city": "Ottawa",
                    "customer_info": {
                        "last_delivery_date": null,
                        "orders_this_month": 0,
                        "buyer_average_order": 0.0
                    },
                    "country": "Canada",
                    "business_name": "Cole's Cappuccino"
                },
                {
                    "province": "ON",
                    "city": "Waterloo",
                    "customer_info": {
                        "last_delivery_date": "2018-04-30T12:00:00-00:00",
                        "orders_this_month": 1,
                        "buyer_average_order": 5.0
                    },
                    "country": "Canada",
                    "business_name": "Jen's Jello"
                }],
            isModalActive: false,
            currentCustomerIndex: null,
        }
    }

    showCustomerModal(i) {
        console.log("Inside this");
        this.state.isModalActive = true;
        this.state.currentCustomerIndex = i;
    }

    hideCustomerModal() {
        this.state.isModalActive = false;
        this.state.currentCustomerIndex = null;
    }

    renderCustomer(i) {
        return <Customer
            value={this.state.customers[i].business_name}
            onClick={() => this.showCustomerModal(i)}
        />;
    }

    renderCustomerData() {
        return <CustomerData
            customerData={this.state.customers[this.state.currentCustomerIndex]}
        />;
    }

    render() {
        let customerItems = [];
        if(this.state.isModalActive) {
            customerItems.push(this.renderCustomerData);
        } else {
            for (let i = 0; i < this.state.customers.length; i++) {
                customerItems.push(this.renderCustomer(i));
            }
        }
        return (
            <div>
                <div>
                    {customerItems}
                </div>
            </div>
        );
    }

}

ReactDOM.render(
    <Panel />,
    document.getElementById('root')
);
