import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';

class Customer extends React.Component {
    render() {
        return (
            <div className="customer">
                {this.props.value} 
                <button onClick = {this.props.onClick}>VIEW</button>
            </div>
        );
    }
}

class CustomerData extends React.Component {
    render() {
        return (
            <div className="customer-data">
                {this.props.value} 
            </div>
        );
    }
}

class Panel extends React.Component {

    constructor(props) {
        super(props);
        this.state = {
            customers: [
                {
                    "province": "ON",
                    "city": "Ottawa",
                    "customer_info": {
                        "last_delivery_date": null,
                        "orders_this_month": 0,
                        "buyer_average_order": 0.0
                    },
                    "country": "Canada",
                    "business_name": "Cole's Cappuccino"
                },
                {
                    "province": "ON",
                    "city": "Waterloo",
                    "customer_info": {
                        "last_delivery_date": "2018-04-30T12:00:00-00:00",
                        "orders_this_month": 1,
                        "buyer_average_order": 5.0
                    },
                    "country": "Canada",
                    "business_name": "Jen's Jello"
                }],
        }
    }

    handleClick(i) {
        const customerData = this.state.customers[i];
        alert(customerData.business_name);
    }

    renderCustomer(i) {
        return <Customer
            value = {this.state.customers[i].business_name}
            onClick = {() => this.handleClick(i)}
        />;
    }

    render() {
        return (
            <div>
                <div className="customer-row">
                    {this.renderCustomer(0)}
                </div>
                <div className="customer-row">
                    {this.renderCustomer(1)}
                </div>
            </div>
        );
    }

}

ReactDOM.render(
    <Panel />,
    document.getElementById('root')
  );
