import React from 'react';
import ReactDOM from 'react-dom';
import APICall from './util';
import BootstrapTable from 'react-bootstrap-table-next';
import paginationFactory from 'react-bootstrap-table2-paginator';
import ReactModal from 'react-modal';
import './index.css';

class Customer extends React.Component {
    render() {
        return (
            <div className="customer">
                {/* {this.props.value} */}
                <button onClick={this.props.onClick}>VIEW</button>
                <button>X</button>
            </div>
        );
    }
}

class CustomerData extends React.Component {
    render() {
        return (
            <div className="customer-data">
                {this.props.data.city}
                <button onClick={this.props.onClick}>X</button>
            </div>
        );
    }
}

class Panel extends React.Component {

    constructor(props) {
        super(props);
        this.state = {
            customers: [],
            isModalActive: false,
            currentCustomerIndex: null,
            showModal: false,
        };
        this.handleOpenModal = this.handleOpenModal.bind(this);
        this.handleCloseModal = this.handleCloseModal.bind(this);
    }

    handleOpenModal(index) {
        this.setState({ showModal: true, currentCustomerIndex: index });
    }

    handleCloseModal() {
        this.setState({ showModal: false, currentCustomerIndex: null });
    }

    showCustomerModal(i) {
        this.setState({ isModalActive: true, currentCustomerIndex: i });
    }

    hideCustomerModal() {
        this.setState({ isModalActive: false, currentCustomerIndex: null });
    }

    renderCustomer(i) {
        return <Customer
            value={this.state.customers[i].business_name}
            onClick={() => this.showCustomerModal(i)}
        />;
    }

    renderCustomerData() {
        return <CustomerData
            data={this.state.customers[this.state.currentCustomerIndex]}
            onClick={() => this.hideCustomerModal()}
        />;
    }

    render() {

        let products = [
            {
                "province": "ON",
                "city": "Ottawa",
                "country": "Canada",
                "business_name": "Cole's Cappuccino"
            },
            {
                "province": "ON",
                "city": "Waterloo",
                "country": "Canada",
                "business_name": "Jen's Jello"
            },
            {
                "province": "ON",
                "city": "Kingston",
                "country": "Canada",
                "business_name": "Brock's Bakery"
            },
            {
                "province": "ON",
                "city": "Toronto",
                "country": "Canada",
                "business_name": "Matty's Mango"
            }
        ]

        let columns = [{
            dataField: 'province',
            text: 'Province'
        }, {
            dataField: 'city',
            text: 'City'
        }, {
            dataField: 'country',
            text: 'Country'
        }, {
            dataField: 'business_name',
            text: 'Business Name'
        }, {
            dataField: 'view_button',
            text: ''
        }];

        let customers = products.slice();
        // this.setState({ customers: customers });
        this.state.customers = customers;

        APICall().then((data) => {
            this.setState({ customers: data });
        });

        for (let i = 0; i < customers.length; i++) {
            let currentProduct = products[i];
            currentProduct["view_button"] = <button onClick={() => this.handleOpenModal(i)}>View</button>
        }

        return (
            <div>
                <BootstrapTable striped bordered hover
                    keyField="city"
                    data={customers}
                    columns={columns}
                />
                <ReactModal id="modal" isOpen={this.state.showModal} contentLabel="Minimal Modal Example">
                    <button onClick={this.handleCloseModal}>
                        <CustomerData data={this.state.customers[this.state.currentCustomerIndex]} />
                        X
                    </button>
                </ReactModal>
            </div>
        );

        if (this.state.isModalActive) {

            let customers = this.renderCustomerData();
            return (
                <div>{customers}</div>
            )

        } else {
            for (let i = 0; i < customers.length; i++) {
                let currentProduct = products[i];
                currentProduct["view_button"] = <button onClick={() => this.handleOpenModal(i)}>View</button>
            }

            return (
                <div>
                    <BootstrapTable striped bordered hover
                        keyField="city"
                        data={customers}
                        columns={columns}
                    />
                    <ReactModal id="modal" isOpen={this.state.showModal} contentLabel="Minimal Modal Example">
                        <button onClick={this.handleCloseModal}>
                            <CustomerData
                                data={this.state.customers[this.state.currentCustomerIndex]}
                                onClick={() => this.hideCustomerModal()}
                            />
                            X
                        </button>
                    </ReactModal>
                </div>
            );
        }

    }

}

ReactDOM.render(
    <Panel />,
    document.getElementById('root')
);
